// Made by GRPTeam202205
<template>
    <!--Search bar-->
    <nav class="navbar">
        <div class="container-fluid">
            <!--Drop-down menu to choose the number of Records Per Page-->
            <div class="dropdown">
                <button class="btn dropdown-toggle" type="button" id="dropdownMenu2" data-bs-toggle="dropdown" aria-expanded="false">
                    {{$t('Records Per Page')}}
                </button>
                <ul class="dropdown-menu" aria-labelledby="dropdownMenu2">
                    <li><button class="dropdown-item" type="button" @click="changeItemPerPage(5)">5</button></li>
                    <li><button class="dropdown-item" type="button" @click="changeItemPerPage(10)">10</button></li>
                    <li><button class="dropdown-item" type="button" @click="changeItemPerPage(15)">15</button></li>
                </ul>
            </div>
            <a class="navbar-brand"></a>
            <form class="d-flex" role="search">
            <input class="form-control me-2" type="search" :placeholder="$t('Search')" aria-label="Search" v-model="search">
            <button class="btn btn-outline-success" type="submit">{{$t('Search')}}</button>
            </form>
        </div>
    </nav>

    <!--Income List-->
    <div class="container">
        <h1 class="test-center">{{$t('Income List')}}
            <router-link :to="{name:'AddProduct'}"><i class="fa fa-cart-plus" style="font-size:42px"></i></router-link>
        </h1>
        <!-- Success Alert will be shown if the product is successfully deleted from the database. -->
        <div class="alert alert-success" v-if="isSuccess">
            {{$t('Product.The product is deleted successfully!')}}
        </div>
        <br>
        <div class="table-responsive">
        <table class="table table-stripped">
            <thead>
                <th>
                    <i class="fa fa-sort-alpha-asc" type="button" aria-hidden="true" @click="sort('name')"/>
                    {{$t('Reference No.')}}
                </th>
                <th>
                    <i class="fa fa-sort-alpha-asc" type="button" aria-hidden="true" @click="sort('type')"/>
                    {{$t('Source')}}
                </th>
                <th>
                    <i class="fa fa-sort-alpha-asc" type="button" aria-hidden="true" @click="sort('quantity')"/>
                    {{$t('Quantity')}}
                </th>
                <th>
                    <!-- <i class="fa fa-sort-alpha-asc" type="button" aria-hidden="true" @click="sort('purchase_price')"/> -->
                    {{$t('Status')}}
                </th>
                <th>
                    <!-- <i class="fa fa-sort-alpha-asc" type="button" aria-hidden="true" @click="sort('sell_price')"/> -->
                    {{$t('Time')}}
                </th>
            </thead>
            <tbody>
                <tr v-for = "product in filteredProducts" v-bind:key = "product.id" class="table-bordered">
                    <td>{{product.name}}</td>
                    <td>
                        <router-link :to="'/typeDetails/'+ product.type">
                            <span class="badge badge-success rounded-pill" type="button">{{product.type}}</span>
                        </router-link>
                    </td>
                    <td>{{product.quantity}}</td>
                    <td>{{product.purchasePrice}}</td>
                    <td>{{product.sellPrice}}</td>
                    <td>
                        <router-link :to="'/supplierDetails/'+ product.name">
                            <fa icon="address-card" type="button" />
                        </router-link>
                    </td>  
                    <td>{{product.remarks}}</td>
                    <td>
                        <!-- Modal -->
                        <div class="modal fade" id="onDelete" tabindex="-1" aria-labelledby="onDeleteLabel" aria-hidden="true">
                            <div class="modal-dialog">
                                <div class="modal-content">
                                <div class="modal-header">
                                    <h5 class="modal-title" id="onDeleteLabel">{{$t('CONFIRM')}}</h5>
                                    <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                </div>
                                <div class="modal-body">{{$t('Are your sure to delete the product?')}}</div>
                                <div class="modal-footer">
                                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">{{$t('No')}}</button>
                                    <button type="button" class="btn btn-primary" data-bs-dismiss="modal" @click="deleteProduct(this.productName)">{{$t('Yes')}}</button>
                                </div>
                                </div>
                            </div>
                        </div>
                        <fa icon="trash" type="button" data-bs-toggle="modal" data-bs-target="#onDelete"  @click="getProductName(product.name)"/>
                    </td>
                </tr>
            </tbody>
        </table>
        </div>
        <!-- Show the total product quantity -->
        <h5>{{$t('Total stock')}}: {{total}}</h5>
        <img src="/static/Others/pdf.png" width="38" height="35" type="button" @click="downloadPDF"/>
        &nbsp
        <img src="/static/Others/xlsx.png" width="35" height="35" type="button" @click="downloadExcel"/>
    </div>
    
    <!--Page navigation bar-->
    <nav class="navbar">
        <div class="container-fluid">
            <h5>Total Pages: {{this.totalPages}}</h5>
            <a class="navbar-brand"></a>
            <nav aria-label="Page navigation example">
                <ul class="pagination">
                    <li class="page-item"><a class="page-link" href="#" @click="pagination(this.pageNum - 1)">{{$t('Previous')}}</a></li>
                    <li class="page-item"><a class="page-link" href="#" @click="pagination(0)">{{this.pageNum+1}}</a></li>
                    <li class="page-item"><a class="page-link" href="#" @click="pagination(pageNum+1)">{{$t('Next')}}</a></li>
                </ul>
            </nav>
        </div>
    </nav>
</template>

<script>
import BilibiliService from '../../services/Bilibili/BilibiliService'
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import * as XLSX from 'xlsx'

function s2ab(s) {
  // Convert a string to an ArrayBuffer
  const buf = new ArrayBuffer(s.length);
  const view = new Uint8Array(buf);
  for (let i = 0; i < s.length; i++) {
    view[i] = s.charCodeAt(i) & 0xFF;
  }
  return buf;
}
export default {
    name: 'Products',
    data(){
        return{
            itemsPerPage: '',//the number of customer shown in one page
            pageNum: 0,//store current page number
            productName: '',
            products: [],//store all products with pagination
            distinctProducts: [],//store products with distinct names
            search:'',//store search content
            isSuccess: false,//true if the product info needs editing, else false
            pdfIconSize: '2em',
            excelIconSize: '2em',
        }
    },
    methods:{
        //Get all products with distinct names from the database
        async getDistinctProducts(){
            await BilibiliService.getDistinctProducts().then((response)=>{
                this.distinctProducts = response.data;
                this.products = response.data
            });
        },
        //Delete a chosen product from the database
        deleteProduct(productName) {
            BilibiliService.deleteProduct(productName).then((response)=>{
                this.isSuccess = true;
                this.getDistinctProducts()
            });
        },
        getProductName(name){
            this.productName = name
        },
        //Sort products by a given field, such as id, name
        sort(field){
            BilibiliService.sortAscending(this.pageNum, this.itemsPerPage, field).then((response)=>{
                this.distinctProducts = response.data;
            });
        },
        //Get products information as required after paging
        pagination(pageNum){
            this.pageNum = pageNum;
            if(this.pageNum == -1) {
                this.pageNum = 0;
                return;
            }
            BilibiliService.pagination(pageNum,this.itemsPerPage).then((response)=>{
                this.distinctProducts = response.data;
            });
        },
        //Change the number of products shown per page 
        changeItemPerPage(num){
            this.itemsPerPage = num;
            this.pagination(this.pageNum,this.itemsPerPage);
        },
        //Jump to the product page for a given type
        jumpToTypeDetailsPage(){
            this.$router.push({path:'/typeDetails'})
        },
        //Download the pdf format of the product table
        downloadPDF(){
            const columns = [
                    { header: 'Product ID', dataKey: 'id' },
                    { header: 'Name', dataKey: 'name' },
                    { header: 'Quantity', dataKey: 'quantity' },
                    { header: 'Purchase Price', dataKey: 'purchasePrice' },
                    { header: 'Sale Price', dataKey: 'sellPrice' },
                    { header: 'Supplier Name', dataKey: 'supplierId' },
                    { header: 'Remarks', dataKey: 'remarks' }
                ];
            const pdf = new jsPDF();
            //text is placed using x, y coordinates
            pdf.setFontSize(20).text("Product List", 80, 10);
            //use autoTable plugin
            autoTable(pdf, {
                columns,
                body: this.distinctProducts,
                margin:{top: 30}
            });
            //show total stock
            pdf.setFontSize(16).text('Total products: ' + this.total, 80, 20);
            //download pdf
            pdf.save("Products.pdf");
        },
        //Download the excel format of the product table
        downloadExcel() {
          const filteredProductsWithoutUseless = this.filteredProducts.map(products => {
            return {
                ProductID: products.id,
                Name: products.name,
                Type: products.type,
                Quantity: products.quantity,
                PurchasePrice: products.purchasePrice,
                SalePrice: products.sellPrice,
                Description: products.remarks,

            };
          });
          // Create a new workbook
          const wb = XLSX.utils.book_new();
          // Add a new worksheet to the workbook
          const ws = XLSX.utils.json_to_sheet(filteredProductsWithoutUseless);
          // Add the worksheet to the workbook
          XLSX.utils.book_append_sheet(wb, ws, 'Products');
          // Write the workbook to a binary string
          const wbout = XLSX.write(wb, { bookType: 'xlsx', type: 'binary' });
          // Convert the binary string to a Blob object
          const blob = new Blob([s2ab(wbout)], { type: 'application/octet-stream' });
          // Create a temporary link to the file and trigger a download
          const url = window.URL.createObjectURL(blob);
          const link = document.createElement('a');
          link.href = url;
          link.download = 'Products.xlsx';
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
        }
    },
    async created(){
        //Get all customers data with distinct names when the page is being loaded
        await this.getDistinctProducts()
        this.itemsPerPage = this.distinctProducts.length
    },
    computed:{
        //Get required products after searching
        filteredProducts(){
            return this.distinctProducts.filter(product => product.name.toLowerCase().includes(this.search.toLowerCase()));
        },
        //Get total number of products
        total(){
            return this.distinctProducts.reduce((total,curr)=>(total=total+curr.quantity),0);
        },
        totalPages(){
            return (Math.ceil(this.products.length / this.itemsPerPage))
        }
    }
}
</script>
