        var now = new Date(date);
        var ds = new Date(now.setMonth(now.getMonth() - i));
        var year = ds.getFullYear();
        var mon = ds.getMonth() + 1;
        var s = year + "-" + (mon < 10 ? '0' + mon : mon);
        return s;
      }
      if (this.$i18n.locale == 'ch') {
        chartID = this.$refs.myChartCh;
        this.barLabel = this.chBarLabel;
        this.labels = this.chLabels;
      } else {
        this.barLabel = this.enBarLabel;
        this.labels = this.enLabels;
      }
      this.chart = new Chart(chartID, {
        type: 'bar',
        locale: this.$i18n.locale,
        data: {
          labels: [getBeforeMonth(date, 5), getBeforeMonth(date, 4), getBeforeMonth(date, 3), getBeforeMonth(date, 2), getBeforeMonth(date, 1), getBeforeMonth(date, 0)],
          datasets: [{
            label: this.barLabel,
            data: [await (this.getPeriodSaleTotal(getBeforeMonth(date, 5) + '-01', getBeforeMonth(date, 4) + '-01') - this.getOneDaySaleTotal(getBeforeMonth(date, 4) + '-01')), await (this.getPeriodSaleTotal(getBeforeMonth(date, 4) + '-01', getBeforeMonth(date, 3) + '-01') - this.getOneDaySaleTotal(getBeforeMonth(date, 3) + '-01')), await (this.getPeriodSaleTotal(getBeforeMonth(date, 3) + '-01', getBeforeMonth(date, 2) + '-01') - this.getOneDaySaleTotal(getBeforeMonth(date, 2) + '-01')), await (this.getPeriodSaleTotal(getBeforeMonth(date, 2) + '-01', getBeforeMonth(date, 1) + '-01') - this.getOneDaySaleTotal(getBeforeMonth(date, 1) + '-01')), await (this.getPeriodSaleTotal(getBeforeMonth(date, 1) + '-01', getBeforeMonth(date, 0) + '-01') - this.getOneDaySaleTotal(getBeforeMonth(date, 0) + '-01')), await this.getPeriodSaleTotal(getBeforeMonth(date, 0) + '-01', date)],
            backgroundColor: 'rgb(153, 102, 255, 0.2)',
            borderColor: 'rgb(153, 102, 255, 1)'
